import { useState } from "react";
import { AsyncPaginate } from "react-select-async-paginate";
import { GEO_API_URL } from "../../api";
import { geoApiOptions } from "../../api";

const Search = ({onSearchChange}) => {

    const [search, setSearch] = useState(null); // inital state null

    const handleOnChange = (searchData) => {
        setSearch(searchData);    // using set search to update search 
        onSearchChange(searchData); 
    }

    const loadOptions = (inputValue) => {
        return fetch(`${GEO_API_URL}/cities?minPopulation=1000000&namePrefix=${inputValue}`, options)
        .then(response => response.json())
        .then(response => console.log(response))
        .catch(err => console.error(err));
    }

    return (
        <AsyncPaginate 
            placeholder = "Search for city"
            debounceTimeout = {600}  // 600 ms 
            value = {search}
            onChange = {handleOnChange}
            loadOptions = {loadOptions}  // method for loading options
        />
    )
}

export default Search;
